import React, { useState } from 'react';

import "./styles/style-feedback.css";

import ThanksFeedbackModel from './ThanksFeedbackModel';

function FeedbackModel({ isOpen, onClose }) {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [feedbackText, setFeedbackText] = useState("");

    const openModal = () => setIsModalOpen(true);
    const closeModal = () => setIsModalOpen(false);

    const handleSubmit = (event) => {
        event.preventDefault();
        // Здесь можно добавить логику для отправки отзыва
        // После успешной отправки открываем модальное окно благодарности
        openModal();
    };

    const handleTextareaChange = (event) => {
        setFeedbackText(event.target.value);
    };
    return (
        <div>
            {isOpen && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={onClose}>
                            &times;
                        </span>
                        <p className='modal-title'>Оставить отзыв</p>
                        <form className='modal-form' onSubmit={handleSubmit}>
                            <textarea className='modal-text' placeholder='Отзыв' value={feedbackText} onChange={handleTextareaChange}></textarea>
                            <button className='modal-btn' type='submit'>ОТПРАВИТЬ</button>
                        </form>
                        <ThanksFeedbackModel isOpen={isModalOpen} onClose={closeModal} />
                    </div>
                </div>
            )}
        </div>
    );
}

export default FeedbackModel;
