import {createSlice, createAsyncThunk} from "@reduxjs/toolkit";
import axios from "../../axios";

export const fetchDoctors = createAsyncThunk('doctors/fetchDoctors', async () => {
    const {data} = await axios.get('/doctors');
    return data;
})

const initialState = {
    doctors: {
        items: [],
        status: 'loading',
    }
};

const doctorsSlice = createSlice({
    name: 'doctors',
    initialState,
    reducers: {},
    extraReducers: {
        [fetchDoctors.pending]: (state) => {
            state.doctors.items = [];
            state.doctors.status = 'loading';
        },
        [fetchDoctors.fulfilled]: (state, action) => {
            state.doctors.items = action.payload;
            state.doctors.status = 'loaded';
        },
        [fetchDoctors.rejected]: (state) => {
            state.doctors.items = [];
            state.doctors.status = 'error';
        },
    },
})

export default doctorsReducer = doctorsSlice.reducer;
