import React, { useRef, useEffect } from 'react';

import Pets from "../pets/Pets";

const PetsSlider = ({ pets, isPetsLoading, id }) => {

  return (
    <div>
      {(isPetsLoading ? [...Array(3)] : pets.items || []).map((obj, index) =>
        isPetsLoading ? (
          <div key={index} className="carousel-cell"> {/* Перемещение ключа сюда */}
            <Pets isLoading={true} />
          </div>
        ) : (
          <div  className="carousel-cell"> {/* Перемещение ключа сюда */}
            <Pets
              userId={id}
              petId={obj._id}
              name={obj.name}
              breed={obj.breed}
              gender={obj.gender}
              species={obj.species}
              age={obj.age}
              avatarUrl={obj.avatarUrl}
            />
          </div>
        )
      )}
    </div>
);
};

export default PetsSlider;
