import React from 'react';
import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import Pets from "../pets/Pets";
const PetsSlider = ({pets, isPetsLoading, id}) => {
    const settings = {
        dots: true,
        // infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1
      };
    
  return (
    <Slider {...settings}>
      {(isPetsLoading ? [...Array(3)] : pets.items || []).map((obj, index) =>
        isPetsLoading ? (
          <div key={index}><Pets isLoading={true} /></div>
        ) : (
          <div key={obj._id}>
            <Pets
              userId={id}
              petId={obj._id}
              name={obj.name}
              breed={obj.breed}
              gender={obj.gender}
              species={obj.species}
              age={obj.age}
              avatarUrl={obj.avatarUrl}
            />
          </div>
        )
      )}
    </Slider>
  );
}
 
export default PetsSlider;
