import { fetchPetsCreate } from "../../redux/slices/pets";
import { useDispatch} from "react-redux";
import {useForm} from "react-hook-form";
import TextField from "@mui/material/TextField";

import "./styles/style-petcreate.css";

const PetCreateModal = ({ isOpen, onClose, id }) => {
    const dispatch = useDispatch();
    const onClickCreate = () => {
        dispatch(fetchPetsCreate(id));
    };
    const {register, handleSubmit, formState: {errors, isValid}} = useForm({
        defaultValue: {
          name: '',
          breed: '',
          gender: '',
          species: '',
          age: ''
        },
        mode: 'onChange'
      });
     
    return ( 
        <div>
        {isOpen && (
            <div className="modal">
                <div className="modal-content">
                    <span className="close" onClick={onClose}>
                        &times;
                    </span>
                    <p className='modal-title'>Оставить отзыв</p>
                    <form className='modal-form'>
                        <TextField 
                        className='modal-input' 
                        type="text" 
                        label='Имя'
                        {...register("name", { required: 'Укажите имя питомца' })}
                        error={Boolean(errors.phone)}
                        helperText={errors.phone ? errors.phone.message : ""}
                        ></TextField>
                        <TextField className='modal-input' type="text" label='Тип'></TextField>
                        <TextField className='modal-input' type="text" label='Пол'></TextField>
                        <TextField className='modal-input' type="text" label='Порода'></TextField>
                        <TextField className='modal-input' type="text" label='Возраст'></TextField>
                    </form>
                    <button className='btn-create' onClick={onClickCreate} type='submit'>Изменить</button>
                </div>
            </div>
        )}
    </div>
     );
}
 
export default PetCreateModal;
