import React from 'react';
import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Flickity from "react-flickity-component";

import Pets from "../pets/Pets";
const PetsSlider = ({pets, isPetsLoading, id}) => {

  return (
    <Flickity
    className="Slider"
    elementType="div"
    disableImagesLoaded="false"
    reloadOnUpdate
    static
    options={{
      prevNextButtons: false,
      autoPlay: true,
    }}
  >
      {(isPetsLoading ? [...Array(3)] : pets.items || []).map((obj, index) =>
        isPetsLoading ? (
          <div key={index}><Pets isLoading={true} /></div>
        ) : (
          <div key={obj._id}>
            <Pets
              userId={id}
              petId={obj._id}
              name={obj.name}
              breed={obj.breed}
              gender={obj.gender}
              species={obj.species}
              age={obj.age}
              avatarUrl={obj.avatarUrl}
            />
          </div>
        )
      )}
</Flickity>
  );
}
 
export default PetsSlider;
