import { NavLink } from "react-router-dom";

import card_success from "../images/card-success.svg";
import card_mir from "../images/card-mir.svg";
import master_card from "../images/master-card.svg";
import visa from "../images/visa.svg";

const OrderCard = () => {

  const [services, setServices] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [selectedService, setSelectedService] = useState('');
  const [selectedDoctor, setSelectedDoctor] = useState('');
  const [selectedAppointment, setSelectedAppointment] = useState('');

  useEffect(() => {
      // Загрузка всех услуг
      axios.get('/api/services')
          .then(response => {
              setServices(response.data);
          })
          .catch(error => {
              console.error('Ошибка при загрузке услуг:', error);
          });
  }, []);

  const handleServiceChange = (event) => {
      const serviceId = event.target.value;
      setSelectedService(serviceId);

      // Загрузка врачей, предоставляющих выбранную услугу
      axios.get(`/api/doctors?serviceId=${serviceId}`)
          .then(response => {
              setDoctors(response.data);
          })
          .catch(error => {
              console.error('Ошибка при загрузке врачей:', error);
          });
  };

  const handleDoctorChange = (event) => {
      const doctorId = event.target.value;
      setSelectedDoctor(doctorId);

      // Загрузка доступных дат и времени приема для выбранного врача
      axios.get(`/api/appointments?doctorId=${doctorId}`)
          .then(response => {
              setAppointments(response.data);
          })
          .catch(error => {
              console.error('Ошибка при загрузке дат и времени приема:', error);
          });
  };

  const handleSubmit = async (data) => {
      try {
          // Отправка данных на сервер
          await axios.post('/api/orders', {
              serviceId: selectedService,
              doctorId: selectedDoctor,
              appointmentId: selectedAppointment,
              ...data
          });
          
          // Обновление списка отзывов
          dispatch(fetchReviews(selectedDoctor));
      } catch (error) {
          console.error('Ошибка при отправке данных:', error);
      }
  };
  
  return (
    <main>
      <section className="appointment">
        <div className="container">
          <div className="appointment__inner order__inner">
            <h1 className="appointment__title">Оформление заказа</h1>
            <div className="order__box">
              <NavLink className="appointment__back back" to="/appointment">
                НАЗАД
              </NavLink>
              <form className="payment__form" onSubmit={handleSubmit}>
                <div className="order-info">
                  <select
                    value={selectedService}
                    onChange={handleServiceChange}
                  >
                    <option value="">Выберите услугу</option>
                    {services.map((service) => (
                      <option key={service._id} value={service._id}>
                        {service.service_name}
                      </option>
                    ))}
                  </select>
                  <select value={selectedDoctor} onChange={handleDoctorChange}>
                    <option value="">Выберите врача</option>
                    {doctors.map((doctor) => (
                      <option key={doctor._id} value={doctor._id}>
                        {doctor.fullName}
                      </option>
                    ))}
                  </select>
                  <select
                    value={selectedAppointment}
                    onChange={(event) =>
                      setSelectedAppointment(event.target.value)
                    }
                  >
                    <option value="">Выберите дату и время приема</option>
                    {appointments.map((appointment) => (
                      <option key={appointment._id} value={appointment._id}>
                        {appointment.start_date_time}
                      </option>
                    ))}
                  </select>
                </div>
                <textarea
                  className="order-area"
                  placeholder="Кратко опишите проблему"
                  {...register("review_text", { required: "Напишите отзыв" })}
                ></textarea>
                <button className="top__slider-btn order-btn" type="submit">
                  ЗАПИСАТЬСЯ
                </button>
              </form>
              <div className="order__card">
                <div className="order__card-top">
                  <div className="order__top-left">
                    <img src={card_success} alt="" />
                    <p>Оплата банковской карты</p>
                  </div>
                  <div className="order__top-right">
                    <img src={card_mir} alt="" />
                    <img src={master_card} alt="" />
                    <img src={visa} alt="" />
                  </div>
                </div>
                <div className="order__card-input">
                  <div className="order__input-number">
                    <label
                      className="order__label order__label-number"
                      for="number"
                    >
                      Номер карты
                    </label>
                    <input className="order__input" type="number" id="number" />
                  </div>

                  <div className="order__input-owner">
                    <label
                      className="order__label order__label-owner"
                      for="owner"
                    >
                      Владелец карты
                    </label>
                    <input className="order__input" type="text" id="owner" />
                  </div>

                  <div className="order__input-date-cvv">
                    <label
                      className="order__label order__label-date"
                      for="date"
                    >
                      Дата истечения срока действия
                    </label>
                    <div className="order__date-wrapper">
                      <input
                        className="order__date-cvv "
                        type="text"
                        id="date1"
                      />
                    </div>
                    <input
                      className="order__date-cvv order__date"
                      type="text"
                      id="date"
                    />

                    <label className="order__label order__label-cvv" for="cvv2">
                      CVV2
                    </label>
                    <input className="order__date-cvv" type="text" id="cvv2" />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </main>
  );
};

export default OrderCard;
