import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { NavLink } from "react-router-dom";
import { fetchDoctorAppointments } from "../redux/slices/doctors";
import { fetchServices, fetchDoctorsByService } from '../redux/slices/services';

import card_success from "../images/card-success.svg";
import card_mir from "../images/card-mir.svg";
import master_card from "../images/master-card.svg";
import visa from "../images/visa.svg";

const OrderCard = () => {
  const dispatch = useDispatch();
  const [selectedService, setSelectedService] = useState('');

  React.useEffect(() => {
    dispatch(fetchServices());
  }, [dispatch]);

  React.useEffect(() => {
    if (selectedService) {
      dispatch(fetchDoctorsByService(selectedService));
      // Если требуется очистить выбранные даты сеансов при смене доктора или услуги:
      // dispatch(clearAppointments()); // Пример очистки предыдущих сеансов
    }
  }, [selectedService, dispatch]);

  const handleServiceChange = (event) => {
    setSelectedService(event.target.value);
  };

  const handleDoctorChange = (event) => {
    const doctorId = event.target.value;
      dispatch(fetchDoctorAppointments(doctorId));
  };

  return (
    <main>
      <section className="appointment">
        <div className="container">
          <div className="appointment__inner order__inner">
            <h1 className="appointment__title">Оформление заказа</h1>
            <div className="order__box">
              <NavLink className="appointment__back back" to="/appointment">
                НАЗАД
              </NavLink>
              <form className="payment__form">
        
                <select value={selectedService} onChange={handleServiceChange}>
                  <option value="">Выберите услугу</option>
                  {services.map((service) => (
                    <option key={service._id} value={service._id}>
                      {service.service_name}
                    </option>
                  ))}
                </select>

                {selectedService && (
                  <select onChange={handleDoctorChange}>
                    <option value="">Выберите доктора</option>
                    {doctors.map((doctor) => (
                      <option key={doctor._id} value={doctor._id}>
                        {doctor.fullName}
                      </option>
                    ))}
                  </select>
                )}

                {doctors.length > 0 && (
                  <select>
                    <option value="">Выберите время сеанса</option>
                    {appointments.map((appointment) => (
                      <option key={appointment._id} value={appointment._id}>
                        {new Date(appointment.start_date_time).toLocaleString()}
                      </option>
                    ))}
                  </select>
                )}
                <div className="personal">
                  <input
                    className="personal__input"
                    type="text"
                    placeholder="ФИО"
                  />
                  <input
                    className="personal__input"
                    placeholder="+7 (999) 999 99 99"
                  />
                </div>

                <div className="order-info">
                  <input
                    className="personal__input"
                    type="date"
                    placeholder="Дата и время"
                  />
                </div>

                <textarea
                  className="order-area"
                  placeholder="Кратко опишите проблему"
                ></textarea>

                <div className="order__card">
                  <div className="order__card-top">
                    <div className="order__top-left">
                      <img src={card_success} alt="" />
                      <p>Оплата банковской карты</p>
                    </div>
                    <div className="order__top-right">
                      <img src={card_mir} alt="" />
                      <img src={master_card} alt="" />
                      <img src={visa} alt="" />
                    </div>
                  </div>
                  <div className="order__card-input">
                    <div className="order__input-number">
                      <label
                        className="order__label order__label-number"
                        htmlFor="number"
                      >
                        Номер карты
                      </label>
                      <input
                        className="order__input"
                        type="number"
                        id="number"
                      />
                    </div>

                    <div className="order__input-owner">
                      <label
                        className="order__label order__label-owner"
                        htmlFor="owner"
                      >
                        Владелец карты
                      </label>
                      <input className="order__input" type="text" id="owner" />
                    </div>

                    <div className="order__input-date-cvv">
                      <label
                        className="order__label order__label-date"
                        htmlFor="date"
                      >
                        Дата истечения срока действия
                      </label>
                      <div className="order__date-wrapper">
                        <input
                          className="order__date-cvv "
                          type="text"
                          id="date1"
                        />
                      </div>
                      <input
                        className="order__date-cvv order__date"
                        type="text"
                        id="date"
                      />

                      <label
                        className="order__label order__label-cvv"
                        htmlFor="cvv2"
                      >
                        CVV2
                      </label>
                      <input
                        className="order__date-cvv"
                        type="text"
                        id="cvv2"
                      />
                    </div>
                  </div>
                </div>
                <button className="top__slider-btn order-btn" type="submit">
                  ЗАПИСАТЬСЯ
                </button>
              </form>
            </div>
          </div>
        </div>
      </section>
    </main>
  );
};

export default OrderCard;
