import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { useForm } from "react-hook-form";

import "./styles/style-feedback.css";

import ThanksFeedbackModel from './ThanksFeedbackModel';
import Rating from '../rating/Rating';
import { fetchReviews, fetchReviewsCreate } from '../../redux/slices/reviews';

function getUserIdFromToken() {
    const token = localStorage.getItem('token'); 
    if (token) {
        const parts = token.split('.');
        
        if (parts.length === 3) {
            // Получаем полезную нагрузку из токена (полезная нагрузка закодирована в формате Base64)
            const payload = JSON.parse(atob(parts[1]));
            
            // Проверяем, содержит ли полезная нагрузка поле userId
            if (payload.userId) {
                return payload.userId; // Возвращаем идентификатор пользователя из токена
            }
        }
    }

    return null; // Если токен отсутствует или не содержит информации о пользователе
}


function FeedbackModel({ isOpen, onClose, doctorId}) {
    const dispatch = useDispatch();
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [rating, setRating] = useState(0); 
    const [userId, setUserId] = useState(null);
    const openModal = () => setIsModalOpen(true);
    const closeModal = () => setIsModalOpen(false);
    const { register, handleSubmit } = useForm({
        mode: 'onChange'
    });
    const [loading, setLoading] = useState(false);

    
    useEffect(() => {
        const userIdFromToken = getUserIdFromToken(); 
        setUserId(userIdFromToken); 
    }, []);

    const handleRatingChange = (value) => {
        setRating(value); 
    };

    const onSubmit = async (data) => {
        try {
            setLoading(true);
            const params = { ...data, rating: rating }; 
            await dispatch(fetchReviewsCreate({ doctorId: doctorId, userId: userId, params: params }));
            dispatch(fetchReviews(doctorId));
            onClose();
        } catch (error) {
            console.error('Ошибка при отправке данных:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            {isOpen && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={onClose}>
                            &times;
                        </span>
                        <p className='modal-title'>Оставить отзыв</p>
                        <form className='modal-form' onSubmit={handleSubmit(onSubmit)}>
                            <Rating onChange={handleRatingChange} /> 
                            <textarea 
                                className='modal-text' 
                                placeholder='Отзыв'
                                {...register(" review_text", { required: 'Напишите отзыв' })}
                            >
                            </textarea>
                        </form>
                        <button className='modal-btn' onClick={openModal}  type='submit'>ОТПРАВИТЬ</button>
                        <ThanksFeedbackModel isOpen={isModalOpen} onClose={closeModal} />
                    </div>
                </div>
            )}
        </div>
    );
}

export default FeedbackModel;
