import React, { useRef, useEffect } from 'react';
import Flickity from 'flickity';
import 'flickity/css/flickity.css';
import Pets from "../pets/Pets";

const PetsSlider = ({ pets, isPetsLoading, id }) => {
  const carouselRef = useRef();

  useEffect(() => {
    const flkty = new Flickity(carouselRef.current, {
      contain: true,
      wrapAround: true,
      autoPlay: 2000,
      prevNextButtons: true,
      pageDots: true,
      setGallerySize: false // Отключение авто-размера галереи может потребоваться
    });

    return () => flkty.destroy();
  }, []);

  return (
    <div ref={carouselRef}>
      {(isPetsLoading ? [...Array(3)] : pets.items || []).map((obj, index) =>
        isPetsLoading ? (
          <div className="carousel-cell">
            <Pets isLoading={true} key={index} />
          </div>
        ) : (
          <div  className="carousel-cell">
            <Pets
              key={obj._id}
              userId={id}
              petId={obj._id}
              name={obj.name}
              breed={obj.breed}
              gender={obj.gender}
              species={obj.species}
              age={obj.age}
              avatarUrl={obj.avatarUrl}
            />
          </div>
        )
      )}
    </div>
  );
};

export default PetsSlider;
