import React, {useState} from "react";
import { fetchPetsCreate } from "../../redux/slices/pets";
import { useDispatch} from "react-redux";
import {useForm} from "react-hook-form";
import TextField from "@mui/material/TextField";
import axios from "axios";

import "./styles/style-petcreate.css";

const PetCreateModal = ({ isOpen, onClose, id }) => {
    const dispatch = useDispatch();
    const [loading, setLoading] = useState(false);
    const [value, setValue] = useState('');
    const [title, setTitle] = useState('');
    const [name, setName] = useState('');
    const [breed, setBreed] = useState('');
    const [gender, setGender] = useState('');
    const [species, setSpecies] = useState('');
    const [age, setAge] = useState('');
    const inputFileRef = React.useRef(null);

    const onChange =React.useCallback((value) => {
        setValue(value);
    },[]);
    const onSubmit = async () => {
        try {
            setLoading(true);
            const fields = [
                name,
                breed,
                gender,
                species,
                age
            ]
            const [data] = dispatch(fetchPetsCreate(id, fields));
            const id=data._id;
        } catch (error) {
            
        }   
    };

    const {register, handleSubmit, formState: {errors, isValid}} = useForm({
        defaultValue: {
          name: '',
          breed: '',
          gender: '',
          species: '',
          age: ''
        },
        mode: 'onChange'
      });
     
    return ( 
        <div>
        {isOpen && (
            <div className="modal">
                <div className="modal-content">
                    <span className="close" onClick={onClose}>
                        &times;
                    </span>
                    <p className='modal-title'>Добавить питомца</p>
                    <form className='modal-form' onSubmit={handleSubmit(onChange)}>
                        <TextField 
                        className='modal-input' 
                        type="text" 
                        label='Имя'
                        value={name}
                        onChange={e => setName(e.target.value)}
                        // {...register("name", { required: 'Укажите имя питомца' })}
                        // error={Boolean(errors.name)}
                        // helperText={errors.name ? errors.name.message : ""}
                        ></TextField>

                        <TextField 
                        className='modal-input' 
                        type="text" 
                        label='Тип'
                        value={species}
                        onChange={e => setSpecies(e.target.value)}
                        // {...register("species", { required: 'Укажите тип питомца' })}
                        // error={Boolean(errors.species)}
                        // helperText={errors.species ? errors.species.message : ""}
                        ></TextField>

                        <TextField 
                        className='modal-input' 
                        type="text" 
                        label='Пол'
                        value={gender}
                        onChange={e => setGender(e.target.value)}
                        // {...register("gender", { required: 'Укажите пол питомца' })}
                        // error={Boolean(errors.gender)}
                        // helperText={errors.gender ? errors.gender.message : ""}
                        ></TextField>

                        <TextField 
                        className='modal-input' 
                        type="text" 
                        label='Порода'
                        value={breed}
                        onChange={e => setBreed(e.target.value)}
                        // {...register("breed", { required: 'Укажите породу питомца' })}
                        // error={Boolean(errors.breed)}
                        // helperText={errors.breed ? errors.breed.message : ""}
                        ></TextField>

                        <TextField 
                        className='modal-input' 
                        type="text" 
                        label='Возраст'
                        value={age}
                        onChange={e => setAge(e.target.value)}
                        // {...register("age", { required: 'Укажите возраст питомца' })}
                        // error={Boolean(errors.age)}
                        // helperText={errors.age ? errors.age.message : ""}
                        ></TextField>

                    </form>
                    <button className='btn-create' type='submit' disabled={!isValid} onClick={onSubmit}>Добавить</button>
                </div>
            </div>
        )}
    </div>
     );
}
 
export default PetCreateModal;
