import "../styles/login-register.css";

import { NavLink } from "react-router-dom";
import {useForm} from "react-hook-form";

const Login = () => {
  const {register, handleSubmit, setError, formState: {errors, isValid}} = useForm({
    defaultValue: {
      phone: '',
      password: ''
    },
    mode: 'onChange'
  });
  const onSubmit = (values) => {
    console.log(values);
  }
  return (
    <main>
      <section className="login">
        <div className="container">
          <div className="login__inner inner">
            <h1 className="login__title title-form">Войти в личный кабинет</h1>
            <form className="login__form form" onSubmit={handleSubmit(onSubmit)}>
              <input
                className="login__form-input form-input"
                type="tel"
                placeholder="Номер телефона"
                {...register("phone", {required: 'Укажите номер телефона'})}
              ></input>
                {errors.phone && <p>{errors.phone.message}</p>}
              <input
                className="login__form-input form-input"
                type="password"
                error={errors.password?.message ? "true" : "false"}
                helpertext={errors.password?.message}
                placeholder="Пароль"
                {...register("password", {required: 'Введите пароль'})}
              ></input>
                {errors.password && <p>{errors.password.message}</p>}
              <button className="login__form-btn form-btn" type="submit">Войти</button>
            </form>
            <NavLink className="login__link form-link" to="/registration">
              Зарегистрироваться
            </NavLink>
          </div>
        </div>
      </section>
    </main>
  );
};

export default Login;
