import therapy from "../images/therapy.svg";
import heart from "../images/heart.svg";
import teeth from "../images/teeth.svg";
import endocrine from "../images/endocrine.svg";
import haircut from "../images/haircut.svg";
import exotic from "../images/exotic.svg";
import cat from "../images/cat.svg";
import skin from "../images/skin.svg";
import pharmacy from "../images/pharmacy.svg";
import physician from "./../images/physician.svg";
import bone from "../images/bone.svg";
import knife from "../images/knife.svg";

import mongoose from "mongoose";
const Services = mongoose.model('Services');

const getServicesFromDatabase = async () => {
    try {
        const servicesFromDB = await Services.find({});
        return servicesFromDB.map(service => ({
            id: service._id, // Используем _id из базы данных в качестве id
            title: service.service_name,
            // Дополнительные данные для сервисов, если они есть в вашей модели
            // img: service.img,
            // service_num: service.service_num,
        }));
    } catch (error) {
        console.error("Error fetching services from database:", error);
        throw error;
    }
};

// Получаем список услуг из базы данных и экспортируем его
export const services = await getServicesFromDatabase();

